import React, { PropTypes } from 'react';

const UpdateButton = ({ isFetching, hasError }) => {
  if (isFetching) {
    return (
      <button style={style.fetching.button} disabled>
        正在同步数据<img src={spinUrl} style={style.fetching.img} />
      </button>
    )
  }
  return (
    <button style={style.completed.button} disabled>
      同步完毕
    </button>
  )
};

UpdateButton.propTypes = {
  isFetching: PropTypes.bool.isRequired,
  hasError: PropTypes.bool.isRequired
};

export default UpdateButton;

const spinUrl = "data:image/svg+xml;base64,";

const style = {
  fetching: {
    button: {
      marginBottom: 0,
      marginTop: 10,
      width: '96%',
      color: '#2a2a2a',
      border: 'none',
      fontSize: '14px',
      backgroundColor: '#f9be4a'
    },
    img: {
      verticalAlign: 'middle',
      height: 20,
      width: 20
    }
  },
  completed: {
    button: {
      marginBottom: 0,
      marginTop: 10,
      width: '96%',
      color: '#f9be4a',
      borderColor: '#f9be4a',
      fontSize: '14px'
    }
  }
};
